package oopvisitor

import oopvisitor.Task
import oopvisitor.payloads.AHSPayload
import oopvisitor.payloads.DigitalPayload
import oopvisitor.payloads.StringPayload
import oopvisitor.processors.OutputProcessor

class App {
    val greeting: String
        get() {
            return "Hello World!"
        }
}

fun example() {
    val tasks = arrayOf(
        Task(AHSPayload("some AHS data")),
        Task(DigitalPayload("some digital data")),
        Task(StringPayload("some string data"))
    )

    val processor = OutputProcessor()

    for (task in tasks) {
        processor.processTask(task)
    }
}

fun main() {
    println(App().greeting)

    example()
}
